* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
}

.product-page {
    padding-top: 200px;
    background-image: url("../images/bg-product.jpg");
    background-attachment: fixed;
    li {
        list-style: none;
    }

    .product-title {
        padding-top: 75px;
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 100%;
        flex: 1;
        justify-content: center;

        h1 {
            font-size: 60px;
            color: goldenrod;
        }

        p {
            color: rgb(224, 218, 218);
            font-size: 20px;
            padding: 0 200px 0 200px;
        }
    }

    .img-container1 {
        display: flex;
        margin-top: 5%;
        width: 100%;
        justify-content: center;

        .img1 {
            margin-top: 10px;
            display: flex;
            justify-content: center;
            flex: 1;

            img {
                border-radius: 50%;
                width: 45%;
                height: 65%;
                opacity: 0.8;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
                transition: transform .2s;
                &:hover {
                    transform: scale(1.5);
                }
            }
        }

        .img2 {
            margin-top: 10px;
            display: flex;
            justify-content: center;
            flex: 1;

            img {
                border-radius: 50%;
                width: 45%;
                height: 65%;
                opacity: 0.8;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
                transition: transform .2s;
                &:hover {
                    transform: scale(1.5);
                }
            }
        }
    }

    .img3 {
        margin-top: 10%;
        width: 90%;
        height: 100vh;
        margin-left: auto;

        img {
            width: 90%;
            height: 100vh;
            opacity: 0.8;
            box-shadow: 10px 10px 5px 5px rgba(0,0,0,0.75);
            filter: brightness(90%);
        }
    }

    .video {
        margin-top: 5%;
        width: 90%;
        margin-left: 5%;
        height: 100vh;
        

        video {
            width: 100%;
            height: 100%;
            
        }
    }

    .img-container2 {
        width: 95%;
        margin-top: 5%;
        margin-left: 2.5%;
        height: 175vh;
        display: flex;

        .text-container {
            flex: 1;
            width: 50%;
            height: 100%;
            display: flex;
            flex-direction: column;
            justify-content: space-around;

            .main-title {
                display: flex;
                color: goldenrod;
                font-size: 20px;
            }

            .category1 {
                color: goldenrod;
                font-size: 28px;
            }

            .benefits1 {
                color: rgb(224, 218, 218);
                font-size: 22px;
                li {
                    list-style: circle;
                }
            }

            .category2 {
                color: goldenrod;
                font-size: 28px;
            }

            .benefits2 {
                color: rgb(224, 218, 218);
                font-size: 22px;
                li {
                    list-style: circle;
                }
            }

            .category3 {
                color: goldenrod;
                font-size: 28px;
            }

            .benefits3 {
                color: rgb(224, 218, 218);
                font-size: 22px;
                li {
                    list-style: circle;
                }
            }
        }

        .images-cont2 {
            flex: 1;
            display: flex;
            flex-direction: column;
            width: 50%;
            height: 100%;
            align-items: center;
            justify-content: center;

            .first {
                display: flex;
                height: 55%;
                flex: 1;
                width: 70%;

                img {
                    display: flex;
                    height: 90%;
                    opacity: 0.8;
                    box-shadow: 10px 10px 5px 5px rgba(0,0,0,0.75);
                    filter: brightness(90%);
                    width: 100%;
                    object-fit: cover;
                }
            }

            .sec {
                display: flex;
                height: 55%;
                flex: 1;
                width: 70%;

                img {
                    height: 90%;
                    opacity: 0.8;
                    width: 100%;
                    box-shadow: 10px 10px 5px 5px rgba(0,0,0,0.75);
                    filter: brightness(90%);
                    object-fit: cover;
                }
            }
        }
    }

    .bottom-image {
        margin-top: 15px;
        margin-bottom: 15px;
        display: flex;
        width: 100%;
        height: 70vh;
        justify-content: space-around;

        .leftone {
            width: 40%;
            height: 100%;
            flex: 1;

            img {
                width: 90%;
                height: 100%;
                box-shadow: 10px 10px 5px 5px rgba(0,0,0,0.75);
                filter: brightness(90%);
            }
        }

        .rightone {
            flex: 1;
            height: auto;

            img {
                width: 100%;
                height: 100%;
                box-shadow: 10px 10px 5px 5px rgba(0,0,0,0.75);
                filter: brightness(90%);
            }
        }
    }

    .buy-section {
        display: flex;
        flex-direction: column;
        height: 100vh;
        margin-top: 10%;
        background-color: rgb(20, 20, 20);
        align-items: center;
        justify-items: center;

        .box {
            padding: 24px 150px 24px 150px;
            width: 100%;
            height: 100%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;

            .buy-title {
                color: goldenrod;
                font-size: 60px;
                width: 100%;
            }

            .product-title2 {
                margin-top: 15px;
                color: rgb(218, 218, 218);
                font-size: 48px;
                width: 100%;
            }

            .prod-desc {
                margin-top: 15px;
                color: rgb(197, 197, 197);
                width: 100%;
            }

            .buy-btn {
                display: flex;
                flex-direction: row;
                justify-content: space-around;
                width: 100%;
                height: 125px;
                margin-top: 15px;
                padding: 24px 144px 16px 144px;

                .price {
                    width: 100%;
                    height: 100%;
                    margin-right: 24px;

                    button {
                        color: goldenrod;
                        height: 100%;
                        width: 100%;
                        background-color: black;

                        span {
                            font-size: 30px;
                        }
                    }

                    &:hover {
                        button {
                            background-color: rgb(194, 194, 194);
                            border-color: goldenrod;
                            filter: blur(50%);
                            color: black;
                        }
                    }
                }

                .acquire {
                    width: 100%;
                    height: 100%;

                    button {
                        height: 100%;
                        width: 100%;
                        color: goldenrod;
                        background-color: black;
    

                        span {
                            font-size: 50px;
                            width: 100%;
                            a {
                                width: 100%;
                                letter-spacing: 10px;
                                height: 100%;
                            }
                        }
                    }

                    &:hover {
                        button {
                            border-color: goldenrod;
                            filter: blur(50%);
                            color: black;
                        }
                    }
                }
            }
        }

    }

}

