* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
}

.product-page {
    position: absolute;
    left: 0;
    top: 298vh;
    width: 100%;
    height: 610%;
    background-image: url("../images/bg-product.jpg");
    background-attachment: fixed;

    .bg-img {
        position: absolute;
        z-index: -2;
        height: 610vh;
        object-fit: cover;
        filter: brightness(60%);
        width: 100%;

        img {
            width: 100%;
            height: 100%;
        }
    }

    .product-title {
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 40%;
        flex: 1;
        margin-left: 30%;
        margin-top: 5%;

        h1 {
            font-size: 60px;
            color: goldenrod;
        }

        p {
            color: rgb(224, 218, 218);
            font-size: 20px;
        }
    }

    .img-container1 {
        display: flex;
        margin-top: 5%;
        width: 100%;
        justify-content: center;

        .img1 {
            display: flex;
            justify-content: center;
            flex: 1;
            width: 30%;
            height: 70vh;

            img {
                border-radius: 50%;
                height: 70vh;
                width: 70%;
                opacity: 0.8;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
            }
        }

        .img2 {
            display: flex;
            justify-content: center;
            flex: 1;
            width: 30%;
            height: 70vh;

            img {
                border-radius: 50%;
                height: 70vh;
                width: 70%;
                opacity: 0.8;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
            }
        }
    }

    .img3 {
        margin-top: 10%;
        width: 90%;
        height: 100vh;
        margin-left: auto;

        img {
            width: 90%;
            height: 100vh;
            opacity: 0.8;
            box-shadow: 0px 4px 25px #000000;
            filter: brightness(90%);
        }
    }

    .video {
        margin-top: 5%;
        border: 2px solid white;
        width: 90%;
        margin-left: 5%;
        height: 100vh;
    }

    .img-container2 {
        margin-top: 10%;
        width: 95%;
        margin-left: 2.5%;
        height: 120vh;
        display: flex;

        .text-container {
            flex: 1;
            width: 50%;
            height: 110%;
            display: flex;
            flex-direction: column;
            justify-content: space-around;

            .main-title {
                display: flex;
                color: goldenrod;
                font-size: 20px;
            }

            .category1 {
                color: goldenrod;
                font-size: 24px;
            }

            .benefits1 {
                color: rgb(224, 218, 218);
                font-size: 18px;
            }

            .category2 {
                color: goldenrod;
                font-size: 24px;
            }

            .benefits2 {
                color: rgb(224, 218, 218);
                font-size: 18px;
            }

            .category3 {
                color: goldenrod;
                font-size: 24px;
            }

            .benefits3 {
                color: rgb(224, 218, 218);
                font-size: 18px;
            }
        }

        .images-cont2 {
            flex: 1;
            display: flex;
            flex-direction: column;
            width: 50%;
            height: 120%;
            align-items: center;

            .first {
                display: flex;
                height: 55%;

                img {
                    height: 90%;
                    opacity: 0.8;
                    box-shadow: 0px 4px 25px #000000;
                    filter: brightness(90%);
                }
            }

            .sec {
                display: flex;
                height: 55%;

                img {
                    height: 90%;
                    opacity: 0.8;
                    box-shadow: 0px 4px 25px #000000;
                    filter: brightness(90%);
                }
            }
        }
    }

    .bottom-image {
        margin-top: 15%;
        display: flex;
        width: 100%;
        margin-left: 2.5%;
        height: 70vh;
        justify-content: space-around;

        .leftone {
            width: 40%;
            height: 100%;
            flex: 1;

            img {
                width: 90%;
                height: 100%;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
            }
        }

        .rightone {
            flex: 1;
            width: 40%;
            height: auto;

            img {
                width: 90%;
                height: 100%;
                box-shadow: 0px 4px 25px #000000;
                filter: brightness(90%);
            }
        }
    }
    .buy-section {
        position: absolute;
        top: 610vh;
        left: 0;
        width: 100%;
        height: 100vh;
        display: flex;
        flex-direction: column;
        .box {
            width: 80%;
            height: 50%;
            margin-left: 10%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: space-around;
            .buy-title {
                color: goldenrod;
            }
            .product-title2 {
                color: goldenrod;
            }
            .prod-desc {
                color: goldenrod;
            }
            .buy-btn {
                display: flex;
                flex-direction: row;
                justify-content: space-between;
                width: 25%;
                .price {
                    button {
                        color: red;
                    }
                }
                .acquire {
                    button {
                        color: goldenrod;
                    }
                }
            }
        }

    }

}
